CREATE TABLE CONTRACT (ID INTEGER NOT NULL, CURRENCY VARCHAR(255), EXCHANGE VARCHAR(255), SYMBOL VARCHAR(255), COMPANY_NAME VARCHAR(255), PRIMARY KEY (ID))
ALTER TABLE BAR ADD CONSTRAINT FK_BAR_CONTRACT FOREIGN KEY (CONTRACT) REFERENCES CONTRACT (ID)
